// Please note only Node.js code is supported here
async function run(msg, cfg, snapshot) {
	this.logger.info('"Execute custom code" Action started');
	/* Please feel free to choose and utilize one of the following credential usage samples.
	The sample you choose should correspond to the type of credentials you have created.
	To use a sample, simply uncomment the relevant code:
	1. 'Basic' auth:
	const { username } = cfg.credentials;
	const { password } = cfg.credentials;
	2. 'API Key' auth:
	const { headerName } = cfg.credentials;
	const { headerValue } = cfg.credentials;
	3. 'OAuth2' auth:
	const { access_token } = cfg.credentials;

	The incoming message can be retrieved in the following way:
	const { body } = msg;

	The message emitted from any of the previous steps in the flow can be retrieved by it's fully qualified path:
	const message = msg.passthrough.step_1.body;
	Where step_1 - is the number of the step in the flow*/
	const url = 'https://restcountries.com/v3.1/name/germany';
	const { data } = await axios.get(url);
	// You can emit as many data messages as required
	await this.emit('data', { body: data });
	this.logger.info('Execution finished');
}
